package com.example.restaurants.coreimport com.google.gson.GsonBuilderimport okhttp3.OkHttpClientimport retrofit2.Retrofitimport retrofit2.converter.gson.GsonConverterFactoryobject Api {    private val url = "maps.googleapis.com/maps/api"    private val httpUrl = "https://$url/"    private var gson = GsonBuilder().setLenient().create()    val retrofit = Retrofit.Builder()        .baseUrl(httpUrl)        .addConverterFactory(GsonConverterFactory.create(gson))        .build()}